INSERT INTO faq (name, question, answer) VALUES ('Home', 'Pick from the categories to see frequently asked questions about that topic.', '');
INSERT INTO faq (name, question, answer) VALUES ('Nephrostomy', 'What do I do if I nausea, vomiting, fever, or chills?', 'If you have these symptoms, please call our nurse coordinators, on call physician, or go to the nearest Emergency Room.');
INSERT INTO faq (name, question, answer) VALUES ('Nephrostomy', 'What do I do if I have pain at my site?', 'Some minor discomfort at the site is not uncommon from the tube and or stitches. Monitor for redness and or drainage and contact our nurse coordinators as needed.');
INSERT INTO faq (name, question, answer) VALUES ('Nephrostomy', 'What if my drainage stops?', 'Please verify all connections are secure and the tubing is not kinked. Please contact our nurse coordinators to assist.');
INSERT INTO faq (name, question, answer) VALUES ('Nephrostomy', 'How often do I need my tubes changed?', 'You will need to have your nephrostomy and or biliary tubes changes every 8-12 weeks or more frequently if your team recommends. ');
INSERT INTO faq (name, question, answer) VALUES ('Nephrostomy', 'What if my stitches come out?', 'It is not uncommon to have them break or become loose in between tube exchanges. If you have adequate drainage in the bag and not leaking around the tube site no further intervention is required. Make sure the dressing and tubing is well secured.');
INSERT INTO faq (name, question, answer) VALUES ('Nephrostomy', 'What if I have blood or pink tinged urine in my nephrostomy bag?', 'It is not uncommon to have pink or blood-tinged urine after a nephrostomy tube placement or changes. Continue to drink 8-10 water glasses a day and if it does not clear up, please contact our nurse coordinators. ');
INSERT INTO faq (name, question, answer) VALUES ('Nephrostomy', 'If my bag is leaking, what do I do?', 'You should have been sent home with an extra bag and can change the bag. You may also order extra bags at Argonmedical.com and they are called D Bags 600.');
INSERT INTO faq (name, question, answer) VALUES ('Nephrostomy', 'If you have issues after hours or on the weekends?', 'Call the hospital operator at 614-293-8000 and ask to speak to the Interventional Radiologist on call.');
INSERT INTO faq (name, question, answer) VALUES ('Biliary', 'What do I do if I have nausea, vomiting, fever, or chills?', 'If you have these symptoms, please call our nurse coordinators, on call physician, or go to the nearest Emergency Room.');
