INSERT INTO faq (name, question, answer) VALUES ('Abscess Drain', 'What do my abscess drain is not draining?', 'If the drainage is less than 10 cc for 24 hours for 3 days in a row, please contact our Interventional Radiology clinic for follow up appointment. 614-293-2773 ');
